package 반복문;

import java.util.Iterator;

public class for문 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// while로도 같은 코드를 쓸 수 있지만 while은 무한루프에 자주 쓰이고
		// for도 while과 같은 무한루프를 쑬 수 있지만 횟수제한반복에 자주 쓰임
		
		for (int i = 0; i < 10; i++) {
			System.out.println("@");
		}

	}

}
