package 배열기본;

public class 배열만들기3_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	String trip[] = {"서울", "대전", "대구", "부산", "광주"};
	char col[] = {'r','g','b','c','m'};
	double tall[] = {170.0, 158.0, 188.8};
	
	for (String d : trip) {
		System.out.println(d);
		
	}
	
	for (char d : col) {
		System.out.println(d);
		
	}
	
	for (double d : tall) {
		System.out.println(d);
		
	}
		
		
	}

}
