package 연산자;

public class 비교연산 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 100;
		int b = 200;
		
		System.out.println("일치?" + (a == b));
		System.out.println("불일치?" + (a != b));
		System.out.println("이상?" + (a >= b));
		System.out.println("초과?" + (a > b));

	}

}
